mapper.select:
SELECT
FROM auth_rbac_user ali LEFT JOIN auth_rbac_correlate_user arcu ON ali.user_id = arcu.user_id AND arcu.correlate_type = 1 LEFT JOIN auth_rbac_org aro ON aro.org_id = arcu.correlate_id
ali.del_flag = 0
AND aro.org_sys_code LIKE '${query.orgId}%'
AND aro.org_sys_code IS NULL
AND ( LOCATE(#{query.keyword, jdbcType=VARCHAR}, ali.user_name) > 0 OR LOCATE(#{query.keyword, jdbcType=VARCHAR}, ali.real_name) > 0 OR LOCATE(#{query.keyword, jdbcType=VARCHAR}, ali.work_no) > 0 OR LOCATE(#{query.keyword, jdbcType=VARCHAR}, ali.phone) > 0 )
AND ali.user_status = #{query.userStatus, jdbcType=VARCHAR}
AND ali.create_time >= #{query.startTime, jdbcType=TIMESTAMP}
AND ali.create_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM auth_rbac_user
WHERE user_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE auth_rbac_user
del_flag = 1, unique_help = user_id,
WHERE user_id = #{userId, jdbcType=VARCHAR}
