mapper.select:
SELECT

FROM storage_local_file

    del_flag = 0

        AND (
        LOCATE(#{query.keyword, jdbcType=VARCHAR}, file_original_full_name) > 0
        OR
        LOCATE(#{query.keyword, jdbcType=VARCHAR}, file_type) > 0
        )


        AND create_time >= #{query.startTime, jdbcType=TIMESTAMP}


        AND create_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM storage_local_file
WHERE local_file_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE storage_local_file

    del_flag = 1,

WHERE local_file_id = #{localFileId, jdbcType=VARCHAR}
