mapper.select:
SELECT

FROM sys_common_dict_item

    del_flag = 0
    AND dict_id = #{query.dictId, jdbcType=VARCHAR}

        AND LOCATE(#{query.itemText, jdbcType=VARCHAR}, item_text) > 0


        AND item_status = #{query.itemStatus, jdbcType=VARCHAR}


        AND create_time >= #{query.startTime, jdbcType=TIMESTAMP}


        AND create_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM sys_common_dict_item
WHERE item_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE sys_common_dict_item
del_flag = 1, unique_help = item_id,
WHERE item_id = #{itemId, jdbcType=VARCHAR}
